# Durchschnittliches Intervall zwischen den t_s-Werten im spezifischen Bereich berechnen
start_index <- which(t_s == 3796.5)
end_index <- which(t_s == 4084.9)
average_interval <- mean(diff(t_s[start_index:end_index]))

# Durchschnittlicher Abfall von VO2 von 3796 bis 4084 berechnen
average_decrease <- (VO2[start_index] - VO2[end_index]) / (end_index - start_index) * 0.40

# Neue Daten fÃ¼r 600 Sekunden generieren
new_data_duration <- 600  # Sekunden
n_new_points <- as.integer(new_data_duration / average_interval)

# Rauschen fÃ¼r die AbstÃ¤nde der t_s-Werte
interval_noise_level <- sd(diff(t_s[start_index:end_index])) * 0.20

# Generieren der neuen t_s-Werte mit Rauschen
new_t_s <- numeric(n_new_points)
new_t_s[1] <- t_s[length(t_s)] + average_interval + rnorm(1, 0, interval_noise_level)
for (i in 2:n_new_points) {
  new_t_s[i] <- new_t_s[i-1] + average_interval + rnorm(1, 0, interval_noise_level)
}

# Neue VO2-Werte mit Rauschen generieren
noise_level <- sd(VO2[start_index:end_index]) * 0.15
new_VO2 <- sapply(1:n_new_points, function(i) VO2[length(VO2)] - average_decrease * i + rnorm(1, 0, noise_level))

# Mit bestehenden Daten kombinieren
extended_t_s <- c(t_s, new_t_s)
extended_VO2 <- c(VO2, new_VO2)

# Neue Datenpunkte ausgeben
for (i in seq_along(new_t_s)) {
  cat(sprintf("%.1f\t%.5f\n", new_t_s[i], new_VO2[i]))
}